public class Book43 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 43페이지 진법 변환 예제
		int var1 = 0b1011;
		int var2 = 0206;
		int var3 = 365;
		int var4 = 0xB3;
		// 실행 전 10진수로 계산해보기
		System.out.println("var1 = "+var1); // 8 + 2 + 1 = 11
		System.out.println("var2 = "+var2); // 128 + 6 = 134
		System.out.println("var3 = "+var3); // 365
		System.out.println("var4 = "+var4); // 176 + 3 = 179
	}

}
